import React, { useState } from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import InputField from '../../atoms/input/Input';
import SimpleButton from '../../atoms/button/SimpleButton';

import { makeStyles } from '@material-ui/core';


const useStyles = makeStyles({
  margin:{
    marginRight:13,
  }
})
export default function FormDialog({open,onSubmit,onClose}) {

  const classes = useStyles();
  const [title,setTitle]=useState("")
  const [url,setUrl]=useState("")
  const [author,setAuthor]=useState("")
  const [readTime,setReadTime]=useState("")
  const validateTitle=(event)=>
  {
      setTitle(event.target.value)
  }


  const validateUrl=(event)=>
  {
    setUrl(event.target.value)
  }

  const validateAuthor=(event)=>
  {
    setAuthor(event.target.value)
  }

  const validateReadTime=(event)=>
  {
    setReadTime(event.target.value)
  }

  const handleSubmit=()=>
  {
    onSubmit(title,url,author,readTime)
  }

  return (
    <div>
      <Dialog open={open} onClose={onClose} aria-labelledby="form-dialog-title" fullWidth
  >
        <DialogTitle id="form-dialog-title"><h2 >ADD BOOK</h2></DialogTitle>
        <DialogContent>

          <InputField value={title} error="false" onChange={validateTitle} label="Title"/>

          <InputField value={url}  onChange={validateUrl} label="Image"/>
          <InputField value={author} onChange={validateAuthor} label="Author"/>
          <InputField value={readTime} onChange={validateReadTime} label="Readtime"/>

        </DialogContent>
        <DialogActions>
          <Button onClick={onClose} color="primary">
            Cancel
          </Button>
          <div className={classes.margin}>
          <SimpleButton  name="Submit" onClick={handleSubmit}/>
          </div>
        </DialogActions>
      </Dialog>
    </div>
  );
}
